# Part 1
print('Part 1 answer:', sum([1 if (range(int(line.strip().split(',')[0].split('-')[0]), int(line.strip().split(',')[0].split('-')[1])+1)[0] in range(int(line.strip().split(',')[1].split('-')[0]), int(line.strip().split(',')[1].split('-')[1])+1) and range(int(line.strip().split(',')[0].split('-')[0]), int(line.strip().split(',')[0].split('-')[1])+1)[-1] in range(int(line.strip().split(',')[1].split('-')[0]), int(line.strip().split(',')[1].split('-')[1])+1)) or (range(int(line.strip().split(',')[1].split('-')[0]), int(line.strip().split(',')[1].split('-')[1])+1)[0] in range(int(line.strip().split(',')[0].split('-')[0]), int(line.strip().split(',')[0].split('-')[1])+1) and range(int(line.strip().split(',')[1].split('-')[0]), int(line.strip().split(',')[1].split('-')[1])+1)[-1] in range(int(line.strip().split(',')[0].split('-')[0]), int(line.strip().split(',')[0].split('-')[1])+1)) else 0 for line in open('input.txt', 'r').readlines()]))
# Part 2
print('Part 2 answer:', sum([1 if len(set(range(int(line.strip().split(',')[0].split('-')[0]), int(line.strip().split(',')[0].split('-')[1])+1)).intersection(range(int(line.strip().split(',')[1].split('-')[0]), int(line.strip().split(',')[1].split('-')[1])+1))) else 0 for line in open('input.txt', 'r').readlines()]))